import React from 'react';
import "../styles/Login.css";
import * as CREDS from "../../constants";
import Footer from '../Footer';

function Login() {
    const handleLogin = () => {
        window.location = `${CREDS.AUTH_ENDPOINT}?client_id=${process.env.REACT_APP_CLIENT_ID}&redirect_uri=${process.env.REACT_APP_REDIRECT_URI}&scope=${CREDS.SCOPES_URL}&response_type=${CREDS.RESPONSE_TYPE}&show_dialog=true`;
    }

    return (
        <>
            <div className="login-container">
                <header className="login">
                    <h1 className="login-title">Spotify Stats</h1>
                    <p className="login-desc">Log into Spotify to find out your Top Tracks, <br />
                        Top Artists and Recently Played Tracks</p>
                    <div className="button-container">
                            <button className="login-button" onClick={handleLogin}>
                                Login to Spotify
                            </button>
                    </div>
                    <Footer />
                </header>
            </div>
        </>
    );
}

export default Login;
